VALUES
	(NULL, 2, 'registration', NULL, 'User management', 'New user (via registration)', 'Visitor has registered via the website.', 'Your registration at {{sitename}}.', '<p>You have successfully registered on {{sitename}}.</p>\r\n\r\n<p>Below you will find your new password to log in with:</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"padding-right:20px;\">Username:</td>\r\n			<td style=\"font-weight:bold;\">{{username}}</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"padding-right:20px;\">Password:</td>\r\n			<td style=\"font-weight:bold;\">{{password}}</td>\r\n		</tr>\r\n	</tbody>\r\n</table>'),
	(NULL, 2, 'registration_pending', NULL, 'User management', 'New user (via registration, waiting for validation)', 'Visitor has registered via the website and is waiting for validation.', 'Your registration at {{sitename}}', '<p>You have successfully registered on {{sitename}}.</p>\r\n\r\n<p>Below you will find your new password to log in with:</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"padding-right:20px;\">Username:</td>\r\n			<td style=\"font-weight:bold;\">{{username}}</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"padding-right:20px;\">Password:</td>\r\n			<td style=\"font-weight:bold;\">{{password}}</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\n\n<p><strong>NOTE:</strong><br/>Your new account is not yet active, we will check your registration as soon as possible, and you will receive an email as soon as your account is active.</p>\r\n'),
	(NULL, 2, 'registration_notify', NULL, 'User management', 'New user (admin notification)', 'Notification for the administrator of a new registration.', 'There is a new registration on {{sitename}}.', '<p>Dear administrator,</p>\r\n\r\n<p>A visitor has registered on the website and is waiting for his / her account to be moderated.<br />\r\nBelow you will find some information entered by this visitor:</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"padding-right:20px;\">Username:</td>\r\n			<td><strong>{{email}}</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"padding-right:20px;\">Firstname:</td>\r\n			<td><strong>{{firstname}}</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"padding-right:20px;\">Lastname:</td>\r\n			<td><strong>{{lastname}}</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"padding-right:20px;\">Street:</td>\r\n			<td><strong>{{street}}</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"padding-right:20px;\">Number:</td>\r\n			<td><strong>{{number}}</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"padding-right:20px;\">Postalcode:</td>\r\n			<td><strong>{{postalcode}}</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"padding-right:20px;\">City:</td>\r\n			<td><strong>{{city}}</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"padding-right:20px;\">Phone number:</td>\r\n			<td><strong>{{phone}}</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>You can authorize this account via the website management.</p>'),
	(NULL, 2, 'registration_enabled', NULL, 'User management', 'User has been activated', 'The unmoderated user has just been activated.', 'Your account on {{sitename}} has been activated.', 'Your account on {{sitename}} has been activated.'),
	(NULL, 2, 'lostpassword', NULL, 'User management', 'Forgot password', 'User requests a new password via the forgot password form.', 'Your new password for {{sitename}}.', '<p>You have requested a new password on our website {{sitename}}.</p><p>Below you will find your new password:<br /><strong>{{newpassword}}</strong></p>');
